definition.script:
pipeline {
    agent any

    stages {
        stage('Deploy') {
            steps {
                sh 'helm repo add bitnami https://charts.bitnami.com/bitnami'
                sh 'helm repo update'
                sh 'helm upgrade --install postgresql bitnami/postgresql'
                sh 'helm upgrade --install redis bitnami/redis'
                sh '**********app deployed**********'
            }
        }
    }
}
